import '../styles/Team.css';
import React, { Component } from "react";

class Team extends Component {

    constructor(props) {
        super(props);
        this.renderDelete = this.renderDelete.bind(this);
        this.state={
            message:"",
            drivers:[]
        }
    }

    renderDelete(props) {
        if ((this.props.data.organizor && (this.props.user.id === this.props.data.owner.id))
            || this.props.user.id === this.props.data.owner
            || this.props.user.role === "ADMIN") {
            return <button className='delete-button' onClick={() => {
                this.DeleteTeam();
            }}>DELETE</button>
        }
        else {
            return;
        }
    }

    renderJoin(){
        this.props.data.memberships.forEach(mem => {
            console.log(mem.user.id + " " + this.props.user.id)
            if(mem.user.id === this.props.user.id){
                return;
            }
        })
        return <div>
            <input type="text" value={this.state.message} onChange={event => this.handleChangeMessage(event.target.value)}/>
            <button className='join-button' onClick={this.joinTeam}>JOIN TEAM</button></div>;
    }
    async DeleteTeam() {

        const restURL = "http://localhost:8081/race/rest/teams/" + this.props.data.id;
        await fetch(restURL, {
            method: "DELETE",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        window.location.assign("/teams");
    }
    componentDidMount() {
        let driversList = []
        this.props.data.memberships.forEach(element => {
            driversList.push(<p key={element.user.id}>{element.user.firstName} {element.user.lastName}</p>)
        });
        this.setState({drivers:driversList})
    }
    Register = () => {
        return;
    }
    async joinTeam() {
        const restURL = "http://localhost:8081/race/rest/teams/";
        await fetch(restURL, {
            method: "PUT",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                message: this.state.message
            })
        })
        window.location.assign("/teams");

    }

    handleChangeMessage(value) {
        this.setState({ message: value });
    }
    render() {
        this.renderJoin()
        return (
            <div className="Team">
                <p className='Name'>{this.props.data.name}</p>
                <div className="Drivers">Drivers :</div>
                
                {this.state.drivers}
                <this.renderDelete />
                <p>Message for the join request : </p>
                <this.renderJoin/>
            </div>)
    }
}

export default Team;
