import React, { Component } from "react";

class Car extends Component {

    constructor(props) {
        super(props);
    }
    async DeleteCar() {

        const restURL = "http://localhost:8081/race/rest/cars/" + this.props.data.id;
        await fetch(restURL, {
            method: "DELETE",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        window.location.assign("/garage");
    }
    componentDidMount() {
    }
    componentDidUpdate() {
    }

    render() {
            return (
                <div className="Car">
                    <p className='Model'>{this.props.data.model}</p>
                    <p className='Maker'>{this.props.data.maker}</p>
                    
                </div>)
    }
}

export default Car;
