import Race from './Race';
import React, { useEffect, useState } from "react";
import '../App.css';
import './UpcomingRaces.css';

function UpcomingRaces() {

  const restURL = "http://localhost:8081/race/rest/races/";
  const [races, setRaces] = useState([])
  const [RaceDetails, setRaceDetails] = useState([])


  const fetchData = () => {
    fetch(restURL, {
      headers: {
        method: "GET",
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setRaces(data)
      })
  }

  useEffect(() => {
    fetchData()
  }, [])

  let raceList = [];
  races.forEach(race => {
    raceList.push(
      <Race data={race} 
      onClickDetails={setRaceDetails}/>
    )
  });

  return (
    <div className="Page">
    <div className="UpcomingRaces">
        {raceList}
    </div>
      {RaceDetails}
    </div>
  );
}

export default UpcomingRaces;
