import React, { useEffect, useState } from "react";

import '../styles/Teams.css';
import Team from "../components/Team";

function Teams() {
    const [teams, setTeams] = useState([])
    const [profileData, setProfileData] = useState([])
    const [myTeam, setMyTeam] = useState([])

    async function fetchUser() {
        let restURL = "http://localhost:8081/race/rest/users/current";
        await fetch(restURL, {
            method: "GET",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setProfileData(data);
            })
    }
    async function fetchMyTeam() {
        let restURL = "http://localhost:8081/race/rest/teams/myTeam";
        await fetch(restURL, {
            method: "GET",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setMyTeam(data);
            })
    }

    const fetchData = () => {
        let restURL = "http://localhost:8081/race/rest/teams/";
        fetch(restURL, {
            headers: {
                method: "GET",
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setTeams(data)
            })
    }

    useEffect(() => {
        fetchData()
        fetchUser()
        fetchMyTeam()
    }, [])


    function renderMyTeam (){
        if(myTeam){
            return(
                <div>r</div>
            )
        }
    }

    let teamList = [];
    teams.forEach(team => {
        teamList.push(
        <Team key={team.id} data={team} user={profileData}/>
      )
    });
    return (
        <div className="Page">
            <div className="Teams">
            <a href="/newTeam">NEW TEAM</a>
            <h2>My team</h2>
            {renderMyTeam()}
            <h2>Other teams</h2>
            {teamList}
            </div>
        </div>
    );
}

export default Teams;
