import './Race.css';
import React, { Component } from "react";
import RaceDetails from './RaceDetails';

class Race extends Component {

    constructor(props){
        super(props);
        this.state = {
            details : []
        }
    }

    componentDidMount() {
        console.log(this.props.data.id)
    }
    Register = () => {
        return;
    }
    Details = () => {
        if(this.props.showingDetails){
            this.setState({details: []});
            this.props.onClickDetails(this.state.details);
            this.props.setShowing(false);
        }
        else{
            this.setState({details: <RaceDetails data={this.props.data}/>});
            this.props.onClickDetails(this.state.details);
            this.props.setShowing(true);
        }
    }
    render() {
        return (<div className="Race">
            <p className='Name'>{this.props.data.name}</p>
            <p className="RegDrivers">Registered : {this.props.data.results.length} / {this.props.data.maxDrivers}</p>
            <p className="Circuit">Circuit : {this.props.data.circuit.name}</p>
            <p className="Date">Date of race : {this.props.data.race_date}</p>
            <p className="eorDate">End of registrations : {this.props.data.eorDate}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    this.Register();
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    this.Details();
                }}>DETAILS</button>
            </div>

        </div>)
    }
}

export default Race;
