import Navbar from './Navbar';
import Race from './Race';
import React, { useEffect, useState } from "react";
import '../App.css';

class UpcomingRaces extends Component {
  constructor(props) {
    super(props);
    this.state = {
      races: [],
      RaceDetails: [],
      raceList: []
    }
    this.sendSignUp = this.sendSignUp.bind(this);
  }




  fetchData = () => {
    const restURL = "http://localhost:8081/race/rest/races/";
    fetch(restURL, {
      headers: {
        mode: 'no-cors',
        method: "GET",
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setRaces(data)
      })
  }

  createRaces() {
    races.forEach(race => {
      this.state.raceList.push(
        <Race data={race}
          onClickDetails={setRaceDetails} />
      )
    });
  }

  render() {
    <div className="Page">
      <div className="App">
        <h1>Race Organization</h1>
        <Navbar />
        <div className="main">
          {raceList}
        </div>
      </div>
      {RaceDetails}
    </div>
  }
}

export default UpcomingRaces;
