import './Race.css';
import React, { Component } from "react";
import RaceDetails from './RaceDetails';

class Race extends Component {

    constructor(props){
        super(props);
        this.state = {
            show : false
        }
        this.renderDelete = this.renderDelete.bind(this);
    }

    renderDelete(props){
        if(this.props.user.id === this.props.data.organizor.id || this.props.user.role == "ADMIN"){
            return <button className='delete-button' onClick={() => {
                this.DeleteRace();
            }}>DELETE</button>
        }
        else{
            return;
        }
    }
    async DeleteRace(){
        const restURL = "http://localhost:8081/race/rest/races/";
        await fetch(restURL, {
            method: "POST",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                maker: this.state.maker,
                model: this.state.model,
                carClass: this.state.carClass
            })
        })
    }
    componentDidMount() {
        //console.log(this.props.data.id)
    }
    Register = () => {
        return;
    }
    
    render() {
        return (
        <div className="Race">
            <p className='Name'>{this.props.data.name}</p>
            <p className="RegDrivers">Registered : {this.props.data.results.length} / {this.props.data.maxDrivers}</p>
            <p className="Circuit">Circuit : {this.props.data.circuit.name}</p>
            <p className="Date">Date of race : {this.props.data.race_date}</p>
            <p className="eorDate">End of registrations : {this.props.data.eor_date}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    this.Register();
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    this.props.showDetails(this.props.data);
                }}>DETAILS</button>
                <this.renderDelete/>
            </div>

        </div>)
    }
}

export default Race;
