import './Race.css';
import React, { Component } from "react";

class Race extends Component {
    constructor(props) {
        super(props);
        this.state =  {
            race: props,
            name: props.name,
            maxDrivers: props.maxDrivers,
            driver: "",
            date: props.date,
            eorDate: "",
            circuitName: props.circuit.name,
            regDrivers: 0,
        };
    }

    componentDidMount() {
        console.log(this.props.raceId);
        this.fetchData();
        this.setState({
            name : this.state.race.name,
            maxDrivers : this.state.race.maxDrivers,
            driver : "",
            date : this.state.race.race_date,
            eorDate : "01-01-2022",
            circuitName : this.state.race.circuit.name,
            regDrivers : this.state.race.results.length,
        })
        

    }
    Register = (race, driver) => {
        return;
    }
    Details = (race) => {
        return;
    }
    render() {
        return (<div className="Race">
            <p className='Name'>{this.state.name}</p>
            <p className="RegDrivers">Registered : {this.state.regDrivers} / {this.state.maxDrivers}</p>
            <p className="Circuit">Circuit : {this.state.circuitName}</p>
            <p className="Date">Date of race : {this.state.date}</p>
            <p className="eorDate">End of registrations : {this.state.eorDate}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    this.Register(this.state.race, this.state.driver);
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    this.Details(this.state.race);
                }}>DETAILS</button>
            </div>

        </div>)
    }
}

export default Race;
