import './Race.css';

const Race = () => {

    const restURL = 'http://localhost:8081/race/rest/races/2';

    var race;
    fetch(restURL, {
        headers: {
            mode: 'no-cors',
            method: "GET",
            'Content-Type': 'application/json',
        }
    })
        .then(response => response.text())
        .then(data => race = data)
        .then(data => console.log(data));


    //variables to load from rest
    const name = data[1];
    const regDrivers = 10;
    const maxDrivers = 20;
    const driver = "";
    const circuit = "Spa Francorshamps";
    const date = "01-01-2022";
    const eorDate = "01-01-2022";

    function Register(race, driver) {
        return;
    }
    function Details(race) {
        return;
    }

    return (
        <div className="Race">
            <p className='Name'>{name}</p>
            <p className="RegDrivers">Registered : {regDrivers} / {maxDrivers}</p>
            <p className="Circuit">Circuit : {circuit}</p>
            <p className="Date">Date of race : {date}</p>
            <p className="eorDate">End of registrations : {eorDate}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    Register(race, driver);
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    Details(race);
                }}>DETAILS</button>
            </div>

        </div>
    );
}

export default Race;
