import './Race.css';
import React, { useEffect, useState } from "react";
const Race = () => {

    const restURL = "http://localhost:8081/race/rest/races/5";
    const [raceData, setRaceData] = useState([])
      const fetchData = () => {
        fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "GET",
                'Content-Type': 'application/json',
            }
        })
          .then(response => {
            return response.json()
          })
          .then(data => {
            setRaceData(raceData)
          })
      }
    
      useEffect(() => {
        fetchData()
      }, [])
    
    const race = data;
    console.log(race);
    const name = race.name;
    
    let regDrivers = race.results.length;
    const maxDrivers = race.maxDrivers;
    const driver = "";
    let circuit = race.circuit;
    const date = race.race_date;
    const eorDate = "01-01-2022";

    function Register(race, driver) {
        return;
    }
    function Details(race) {
        return;
    }
    return (
        <div className="Race">
            <p className='Name'>{name}</p>
            <p className="RegDrivers">Registered : {regDrivers} / {maxDrivers}</p>
            <p className="Circuit">Circuit : {circuit.name}</p>
            <p className="Date">Date of race : {date}</p>
            <p className="eorDate">End of registrations : {eorDate}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    Register(race, driver);
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    Details(race);
                }}>DETAILS</button>
            </div>

        </div>
    );
}

export default Race;
