import React, { useEffect, useState } from "react";

import '../styles/Teams.css';
import Team from "../components/Team";
import JoinRequest from "../components/JoinRequest";

function Teams() {
    const [teams, setTeams] = useState([])
    const [profileData, setProfileData] = useState([])
    const [myTeam, setMyTeam] = useState([])

    async function fetchUser() {
        let restURL = "http://localhost:8081/race/rest/users/current";
        await fetch(restURL, {
            method: "GET",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setProfileData(data);
            })
    }
    async function fetchMyTeam() {
        let restURL = "http://localhost:8081/race/rest/teams/myTeam";
        await fetch(restURL, {
            method: "GET",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setMyTeam(data);
            })
    }

    async function fetchData(){
        let restURL = "http://localhost:8081/race/rest/teams/";
        await fetch(restURL, {
            headers: {
                method: "GET",
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                setTeams(data)
            })
    }

    useEffect(() => {
        fetchMyTeam()
        fetchData()
        fetchUser()
    }, [])

    

    function renderMyTeam() {
        if (myTeam.id != null) {
            let joinRequests = []
            myTeam.joinRequests.forEach(req => {
            joinRequests.push(<JoinRequest key={req.id} data={req} team={myTeam}/>)
            })

            let members = []
            myTeam.memberships.forEach(mem => {
            joinRequests.push(<p key={mem.id}>{mem.username}</p>)
            })
            return (
                <div className="MyTeam">
                    <p className="MyTeamName">
                        Name : {myTeam.name}
                    </p>
                    <p className="MyTeammMebers">
                        Members :
                    </p>
                    {members}
                    <p className="MyTeammRequests">
                        Join request :
                    </p>
                    {joinRequests}
                </div>

            )
        }
    }
    
    let teamList = [];
    teams.forEach(team => {
        teamList.push(
            <Team key={team.id} data={team} user={profileData} />
        )
    });
    return (
        <div className="Page">
            <div className="Teams">
                <a href="/newTeam">NEW TEAM</a>
                <h2>My team</h2>
                {renderMyTeam()}
                <h2>Other teams</h2>
                {teamList}
            </div>
        </div>
    );
}

export default Teams;
