import './Race.css';

const Race = () => {
    const name = "12H of Sebring";
    const regDrivers = 10;
    const maxDrivers = 20;


    Register = (race, driver) => {
        return;
    }

    return ( 
        <div className="Race">
            <p className='Name'>{name}</p>
            <p className="RegDrivers">Registered : {regDrivers} / {maxDrivers}</p>
            <button className='reg-button' onClick={() => {
                Register(race, driver);
            }}>REGISTER</button>
        </div>
     );
}
 
export default Race;
