import './Race.css';
import React, { Component, useEffect, useState } from "react";

class Race extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            race : "",
            name : "",
            maxDrivers : 0,
            driver : "",
            date : "",
            eorDate : "",
            circuitName : "",
            regDrivers : 0,
        };
    }

    componentDidMount() {
        const restURL = "http://localhost:8081/race/rest/races/" + this.props.raceId;
        
    const [raceData, setRaceData] = useState([]);
        const fetchData = () => {
            fetch(restURL, {
                headers: {
                    mode: 'no-cors',
                    method: "GET",
                    'Content-Type': 'application/json',
                }
            })
                .then(response => {
                    return response.json()
                })
                .then(data => {
                    setRaceData(data)
                })
        }
        fetchData()
        this.state.race = raceData;
        this.state.name = this.state.race.name;
        this.state.maxDrivers = this.state.race.maxDrivers;
        this.state.driver = "";
        this.state.date = this.state.race.race_date;
        this.state.eorDate = "01-01-2022";
        this.state.circuitName = raceData.circuit.name;
        this.state.regDrivers = raceData.results.length;
        
    }
    Register = (race, driver) => {
        return;
    }
    Details = (race) => {
        return;
    }
    render() {
        return(<div className="Race">
            <p className='Name'>{this.state.name}</p>
            <p className="RegDrivers">Registered : {this.state.regDrivers} / {this.state.maxDrivers}</p>
            <p className="Circuit">Circuit : {this.state.circuitName}</p>
            <p className="Date">Date of race : {this.state.date}</p>
            <p className="eorDate">End of registrations : {this.state.eorDate}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    Register(this.state.race, this.state.driver);
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    Details(this.state.race);
                }}>DETAILS</button>
            </div>

        </div>)
    }
}

export default Race;
