import '../styles/Team.css';
import React, { Component } from "react";

class Team extends Component {

    constructor(props) {
        super(props);
        this.renderDelete = this.renderDelete.bind(this);
        this.state={
            drivers:[]
        }
    }

    renderDelete(props) {
        if ((this.props.data.organizor && (this.props.user.id === this.props.data.owner.id))
            || this.props.user.id === this.props.data.owner
            || this.props.user.role === "ADMIN") {
            return <button className='delete-button' onClick={() => {
                this.DeleteRace();
            }}>DELETE</button>
        }
        else {
            return;
        }
    }
    async DeleteRace() {

        const restURL = "http://localhost:8081/race/rest/teams/" + this.props.data.id;
        await fetch(restURL, {
            method: "DELETE",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        window.location.assign("/teams");
    }
    componentDidMount() {
        let driversList = []
        this.props.data.memberships.forEach(element => {
            driversList.push(<p key={element.user.id}>{element.user.firstName} {element.user.lastName}</p>)
        });
        this.setState({drivers:driversList})
    }
    Register = () => {
        return;
    }

    render() {
        return (
            <div className="Team">
                <p className='Name'>{this.props.data.name}</p>
                <div className="Drivers">Drivers :</div>
                
                {this.state.drivers}
                <this.renderDelete />
                <div className="buttons">
                    <a href="/newTeam">NEW TEAM</a>
                </div>
            </div>)
    }
}

export default Team;
