import React, { Component } from "react";

class JoinRequest extends Component {

    constructor(props) {
        super(props);
        this.Accept = this.Accept.bind(this);
    }
    async DeleteCar() {

        const restURL = "http://localhost:8081/race/rest/cars/" + this.props.data.id;
        await fetch(restURL, {
            method: "DELETE",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        window.location.assign("/garage");
    }
    componentDidMount() {
    }
    componentDidUpdate() {
    }
    Accept(){
        const restURL = "http://localhost:8081/race/rest/memberships/"+this.props.team.id+"/requests";
        fetch(restURL, {
            method: "PUT",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id : this.props.data.id,
                status : "ACCEPTED"
            })
        })
        window.location.assign("/teams");
    }
    Deny(){
        const restURL = "http://localhost:8081/race/rest/memberships/"+this.props.team.id+"/requests";
        fetch(restURL, {
            method: "PUT",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id : this.props.data.id,
                status : "REJECTED"
            })
        })
    }
    render() {
            return (
                <div className="JoinRequest">
                    <p className='Message'>{this.props.data.message}</p>
                    <button className="accept" onClick={this.Accept}>ACCEPT</button>
                    <button className="accept" onClick={this.Deny}>DENY</button>
                    
                </div>)
    }
}

export default JoinRequest;
