import Race from './Race';
import React, { useEffect, useState } from "react";
import '../App.css';
import './UpcomingRaces.css';

function UpcomingRaces() {

  const restURL = "http://localhost:8081/race/rest/races/";
  const [races, setRaces] = useState([])
  const [RaceDetails, setRaceDetails] = useState([])
  const [profileData, setProfileData] = useState([])

  const fetchUser = () => {
    let restURL = "http://localhost:8081/race/rest/users/current";
    fetch(restURL, {
        method: "GET",
        credentials: 'include',
        headers: {
            mode: 'no-cors',
            'Content-Type': 'application/json',
        }
    })
        .then(response => {
            return response.json()
        })
        .then(data => {
            this.setProfileData(data);
        })
}

  const fetchData = () => {
    fetch(restURL, {
      headers: {
        method: "GET",
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setRaces(data)
      })
  }

  useEffect(() => {
    fetchData()
    fetchUser()
  }, [])

  let raceList = [];
  races.forEach(race => {
    raceList.push(
      <Race data={race} user={profileData} 
      onClickDetails={setRaceDetails}/>
    )
  });

  return (
    <div className="Page">
    <div className="UpcomingRaces">
        {raceList}
    </div>
      {RaceDetails}
    </div>
  );
}

export default UpcomingRaces;
