import './Race.css';
import React, { Component } from "react";

class Team extends Component {

    constructor(props) {
        super(props);
        this.renderDelete = this.renderDelete.bind(this);
    }

    renderDelete(props) {
        if ((this.props.data.organizor && (this.props.user.id === this.props.data.owner.id))
            || this.props.user.id === this.props.data.owner
            || this.props.user.role === "ADMIN") {
            return <button className='delete-button' onClick={() => {
                this.DeleteRace();
            }}>DELETE</button>
        }
        else {
            return;
        }
    }
    async DeleteRace() {

        const restURL = "http://localhost:8081/race/rest/teams/" + this.props.data.id;
        await fetch(restURL, {
            method: "DELETE",
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
    }
    componentDidMount() {
        //console.log(this.props.data.id)
    }
    Register = () => {
        return;
    }

    render() {
        console.log(this.props.data);
        return (
            <div className="Race">
                <p className='Name'>{this.props.data.name}</p>
                <p className="Drivers">Drivers : {this.props.data.drivers}</p>
                <button className='details-button' onClick={() => {
                    this.props.showDetails(this.props.data);
                }}>DETAILS</button>
                <this.renderDelete />

            </div>)
    }
}

export default Team;
