import './Race.css';
import React, { Component } from "react";
import RaceDetails from './RaceDetails';

class Race extends Component {

    constructor(props){
        super(props);
        this.state = {
            clicked : false,
            details : []
        }
        this.renderDelete = this.renderDelete.bind(this);
    }

    renderDelete(props){
        console.log(this.props.user)
        if(this.props.user.id === this.props.data.organizer.id){
            return <button className='delete-button' onClick={() => {
                this.DeleteRace();
            }}>DELETE</button>
        }
        else{
            return;
        }
    }
    DeleteRace(){
        console.log("deleting race");
    }
    componentDidMount() {
        //console.log(this.props.data.id)
    }
    Register = () => {
        return;
    }
    Details = () => {
        if(this.state.clicked){
            this.setState({details: [], clicked : false});
            this.props.onClickDetails(this.state.details);
        }
        else{
            this.setState({details: <RaceDetails data={this.props.data}/>, clicked : true});
            this.props.onClickDetails(this.state.details);
        }
    }
    render() {
        return (<div className="Race">
            <p className='Name'>{this.props.data.name}</p>
            <p className="RegDrivers">Registered : {this.props.data.results.length} / {this.props.data.maxDrivers}</p>
            <p className="Circuit">Circuit : {this.props.data.circuit.name}</p>
            <p className="Date">Date of race : {this.props.data.race_date}</p>
            <p className="eorDate">End of registrations : {this.props.data.eor_date}</p>
            <div className="buttons">
                <button className='reg-button' onClick={() => {
                    this.Register();
                }}>REGISTER</button>
                <button className='details-button' onClick={() => {
                    this.Details();
                }}>DETAILS</button>
                <this.renderDelete/>
            </div>

        </div>)
    }
}

export default Race;
