import './SingUp.css';
import React, { Component } from "react";

class SignUp extends Component {


    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            age: 0,
            email: '',
            firstName: '',
            lastName: '',
            role:''
        }
        this.sendSignUp = this.sendSignUp.bind(this);
    }

    componentDidMount() {
    }

    async sendSignUp() {
        const restURL = "http://localhost:8081/race/rest/users";
        await fetch(restURL, {
            method: "POST",
            credentials: 'include',
            headers: {
                'Access-Control-Allow-Origin': 'http://localhost:3000',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                age: this.state.age,
                email: this.state.email,
                username: this.state.username,
                password: this.state.password,
                role: this.state.role
            })
        }).then(response => {
            return response.json()
        })
    }

    handleChangePass(value) {
        this.setState({ password: value });
    }
    handleChangeUsername(value) {
        this.setState({ username: value });
    }
    handleChangeFirstName(value) {
        this.setState({ firstName: value });
    }
    handleChangeLastName(value) {
        this.setState({ lastName: value });
    }
    handleChangeEmail(value) {
        this.setState({ email: value });
    }
    handleChangeAge(value) {
        this.setState({ age: value });
    }
    handleChangeRole(value) {
        this.setState({ age: value });
    }
    render() {
        return (
            <div className="SignUp">
                <h2>Register</h2>
                <div className="firstName">
                    <p>First name : </p>
                    <input type="text" value={this.state.firstName} className="firstName-input" 
                    onChange={event => this.handleChangeFirstName(event.target.value)}/>
                </div>
                <div className="lastName">
                    <p>Last name : </p>
                    <input type="text" value={this.state.lastName} className="lastName-input" 
                    onChange={event => this.handleChangeLastName(event.target.value)}/>
                </div>
                <div className="email">
                    <p>Email : </p>
                    <input type="email" className="email-input" 
                    onChange={event => this.handleChangeEmail(event.target.value)}/>
                </div>
                <div className="age">
                    <p>Age : </p>
                    <input type="number" value={this.state.age} className="age-input" 
                    onChange={event => this.handleChangeAge(event.target.value)}/>
                </div>
                <div className="username">
                    <p>Username : </p>
                    <input type="text" value={this.state.username} className="username-input" 
                    onChange={event => this.handleChangeUsername(event.target.value)}/>
                </div>
                <div className="password">
                    <p>Password : </p>
                    <input type="password" value={this.state.password} className="password-input" 
                    onChange={event => this.handleChangePass(event.target.value)}/>
                </div>
                <div className="role">
                    Role :
                    <select name="role-select" className="role-select"
                        onChange={event => this.handleChangeRole(event.target.value)}>
                        <option value="ROLE_ORGANIZER">ORGANIZOR</option>
                        <option value="ROLE_DRIVER">DRIVER</option>
                        <option value="ROLE_GUEST">GUEST</option>
                    </select>
                </div>
                <button className="SignUp-button" onClick={this.sendSignUp}>Sing Up</button>
            </div>
        )
    }
}

export default SignUp;
