import 'src\styles\AddCar.css';
import React, { Component } from "react";

class AddCar extends Component {


    constructor(props) {
        super(props);
        this.state = {
            maker: '',
            model: '',
            carClass: ''
        }
    }

    async addNewCar() {
        const restURL = "http://localhost:8081/race/rest/cars/";
        await fetch(restURL, {
            method: "POST",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                maker: this.state.maker,
                model: this.state.model,
                carClass: this.state.carClass
            })
        })
    }
    handleChangeMaker(value) {
        this.setState({ maker: value });
    }
    handleChangeModel(value) {
        this.setState({ model: value });
    }
    handleChangeCarClass(value) {
        this.setState({ carClass: value });
    }
    componentDidMount() {
    }

    render() {
        return (
            <div className="AddCar">
                <h2>Add a new car</h2>
                <div className="maker">
                    <p>Maker : </p>
                    <input type="text" value={this.state.maker}
                        className="maker-input"
                        onChange={event => this.handleChangeMaker(event.target.value)} />
                </div>
                <div className="model">
                    <p>Model : </p>
                    <input type="text" value={this.state.maker}
                        className="model-input"
                        onChange={event => this.handleChangeModel(event.target.value)} />
                </div>
                <div>
                    Car class :
                    <select name="carClass-select" className="carClass-select"
                        onChange={event => this.handleChangeCarClass(event.target.value)}>
                        <option value="GT3">GT3</option>
                        <option value="GT4">GT4</option>
                        <option value="LMP1">LMP1</option>
                        <option value="LMP2">LMP2</option>
                    </select>
                </div>
                <button className="create-button" onClick={this.addNewCar}>Add car</button>
            </div>
        )
    }
}

export default AddCar;
