import './CreateRace.css';
import React, { Component } from "react";

class CreateRace extends Component {


    constructor(props) {
        super(props);
        this.state = {
            name: '',
            circuitId: -1,
            date: "",
            eorDate: "",
            classes: [],
            maxDrivers: 0,
            fetchedCircuits: [],
            circuitOptions: []
        }
        this.createRace = this.createRace.bind(this);
    }

    componentDidMount() {
        this.fetchCircuits();
        this.state.fetchedCircuits.forEach(circuit => {
            this.setState({
                circuitOptions:
                    [this.state.circuitOptions, <option value={circuit.id}>{circuit.name}</ option>]
            }
            )
        });
    }
    fetchCircuits() {
        const restURL = "http://localhost:8081/race/rest/circuits/";
        fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "GET",
                'Content-Type': 'application/json'
            }
        }).then(response => {
            response.json()
        }).then(data => { console.log(data);
            this.setState({ fetchedCircuits: data })
        })
        console.log(this.state.fetchedCircuits);
    }
    createRace() {
        const restURL = "http://localhost:8081/race/rest/races/";
        fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "POST",
            },
            body: {

            }
        })
    }
    handleChangeName(value) {
        this.setState({ name: value });
    }
    handleChangeCircuit(value) {
        this.setState({ circuitId: value });
    }
    render() {
        return (
            <div className="CreateRace">
                <h2>Create a new race</h2>
                <div className="name">
                    <p>Name : </p>
                    <input type="text" value={this.state.name}
                        className="name-input"
                        onChange={event => this.handleChangeName(event.target.value)} />
                </div>
                <div className="circuit">
                    <p>Circuit : </p>
                    <select name="circuit-select" className="circuit-select"
                        onChange={event => this.handleChangeCircuit(event.target.value)}>
                        {this.state.circuitOptions}
                    </select>
                </div>

                <button className="create-button" onClick={this.createRace}>Create race</button>
            </div>
        )
    }
}

export default CreateRace;
