import './MyProfile.css';
import React, { Component } from "react";

class MyProfile extends Component {


    async fetchData() {
        const restURL = "http://localhost:8081/race/rest/users/current";
        await fetch(restURL, {
            method: "GET",
            credentials: 'include',
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                this.setState({ profileData: data });
            })
    }

    constructor(props) {
        super(props);
        this.state = {
            profileData: [],
            readOnly: true,
            buttonText: "EDIT",
            firstName: "",
            lastName: "",
            age: "",
            email: "",
            username: ""
        }
        this.enableEdit = this.enableEdit.bind(this);
    }
    enableEdit() {
        if (this.state.readOnly) {
            this.setState({ readOnly: false });
            this.setState({ buttonText: "SAVE" });
        } else {
            this.saveData();
            this.setState({ readOnly: true });
            this.setState({ buttonText: "EDIT" });
        }
    }
    async saveData() {
        const restURL = "http://localhost:8081/race/rest/users/"+this.state.profileData.id;
        await fetch(restURL, {
            method: "PUT",
            credentials: 'include',
            headers: {
                'Access-Control-Allow-Origin': 'http://localhost:3000',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                firstName: this.state.profileData.firstName,
                lastName: this.state.profileData.lastName,
                age: this.state.profileData.age,
                email: this.state.profileData.email,
                username: this.state.profileData.username
            })
        }).then(response => {
            return response.json()
        })
    }
    componentDidMount() {
        this.fetchData();
        //console.log(this.state.profileData)
    }

    handleChangeFirstName(value) {
        this.setState({ firstName: value });
    }
    handleChangeLastName(value) {
        this.setState({ lastName: value });
    }
    handleChangeEmail(value) {
        this.setState({ email: value });
    }
    handleChangeUsername(value) {
        this.setState({ username: value });
    }
    handleChangeAge(value) {
        this.setState({ age: value });
    }

    render() {
        return (
            <div className="MyProfile">
                <h2>My Profile</h2>
                <div className="firstName">
                    <p>First name : </p>
                    <input type="text" readOnly={this.state.readOnly} value={this.state.profileData.firstName} className="firstName-input" />
                </div>
                <div className="lastName">
                    <p>Last name : </p>
                    <input type="text" readOnly={this.state.readOnly} value={this.state.profileData.lastName} className="lastName-input" />
                </div>
                <div className="email">
                    <p>Email : </p>
                    <input type="text" readOnly={this.state.readOnly} value={this.state.profileData.email} className="email-input" />
                </div>
                <div className="username">
                    <p>Username : </p>
                    <input type="text" readOnly={this.state.readOnly} value={this.state.profileData.username} className="username-input" />
                </div>
                <div className="age">
                    <p>Age : </p>
                    <input type="number" readOnly={this.state.readOnly} value={this.state.profileData.age} className="age-input" />
                </div>
                <button className="edit" onClick={this.enableEdit}>{this.state.buttonText}</button>
            </div>
        )
    }
}

export default MyProfile;
