import '../App.css';
import CreateRace from './CreateRace';
import AddCircuit from "./AddCircuit";
import React, { useEffect, useState } from "react";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
        isLoggedIn: false,
        profileData: [],
    }
}
  async checkLoggedIn() {
    let restURL = "http://localhost:8081/race/rest/users/isLoggedIn";
    await fetch(restURL, {
      method: "GET",
      credentials: 'include',
      headers: {
        mode: 'no-cors',
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        this.setState({isLoggedIn:data});
      })
  }

  ComponentDidMount(){
    this.checkLoggedIn()
    this.fetchUser()
  }

  async fetchUser() {
    if(this.state.isLoggedIn){
      let restURL = "http://localhost:8081/race/rest/users/current";
      await fetch(restURL, {
        method: "GET",
        credentials: 'include',
        headers: {
          mode: 'no-cors',
          'Content-Type': 'application/json',
        }
      })
        .then(response => {
          return response.json()
        })
        .then(data => {
            this.setState({profileData:data});
        })
    }
  }
  render() {
      if (isLoggedIn) {
    console.log(profileData)
    if (profileData.role === "ADMIN") {
      return (
        <div className="Home">
          <CreateRace />
          <AddCircuit />
        </div>
      );
    } else if (profileData.role === "ORGANIZOR") {
      return (
        <div className="Home">
          <CreateRace />
        </div>
      );
    }
  } else {
    return (
      <h2>Welcome to the app</h2>
    );
  }
  }
  
}

export default Home;
