import Navbar from './components/Navbar';
import Race from './components/Race';
import React, { useEffect, useState } from "react";
import './App.css';
import MyProfile from './components/MyProfile';
import Login from './components/Login';

function App() {

  const restURL = "http://localhost:8081/race/rest/races/";
  const [races, setRaces] = useState([])
  const [RaceDetails, setRaceDetails] = useState([])


  const fetchData = () => {
    fetch(restURL, {
      headers: {
        mode: 'no-cors',
        method: "GET",
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setRaces(data)
      })
  }

  useEffect(() => {
    fetchData()
  }, [])

  let raceList = [];
  races.forEach(race => {
    raceList.push(
      <Race data={race} 
      onClickDetails={setRaceDetails}/>
    )
  });

  return (
    <div className="Page">
    <div className="App">
      <h1>Race Organization</h1>
      <Navbar />
      <div className="main">
        {raceList}
      </div>
    </div>
      {RaceDetails}
      <Login />
      <MyProfile />
    </div>
  );
}

export default App;
