import Race from './Race';
import RaceDetails from './RaceDetails';
import React, { useEffect, useState } from "react";
import '../App.css';
import './UpcomingRaces.css';

function UpcomingRaces() {

  const restURL = "http://localhost:8081/race/rest/races/";
  const [races, setRaces] = useState([])
  const [profileData, setProfileData] = useState([])
  const [show, setShow] = useState(false)
  const [detailToShow, setDetailToShow] = useState([])

  async function fetchUser() {
    let restURL = "http://localhost:8081/race/rest/users/current";
    await fetch(restURL, {
      method: "GET",
      credentials: 'include',
      headers: {
        mode: 'no-cors',
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setProfileData(data);
      })
  }
  function showDetails(detailToShow){
    setDetailToShow(detailToShow);
    setShow(true);
  }
  function closeDetails(){
    setShow(false);
  }
  const fetchData = () => {
    fetch(restURL, {
      headers: {
        method: "GET",
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setRaces(data)
      })
  }

  useEffect(() => {
    fetchData()
    fetchUser()
  }, [])

  let raceList = [];
  races.forEach(race => {
    raceList.push(
      <Race data={race} user={profileData} showDetails={showDetails}/>
    )
  });

  return (
    <div className="Page">
      <div className="UpcomingRaces">
        {raceList}
      </div>
    <RaceDetails data={detailToShow} show={show}/>
    </div>
  );
}

export default UpcomingRaces;
