import CreateRace from '../CreateRace';
import AddCircuit from "../AddCircuit";
import { Component } from "react";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      profileData: [],
    }
  }
  async checkLoggedIn() {
    let restURL = "http://localhost:8081/race/rest/users/isLoggedIn";
    await fetch(restURL, {
      method: "GET",
      credentials: 'include',
      headers: {
        mode: 'no-cors',
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        this.setState({ isLoggedIn: data });
      })
    this.fetchUser()
  }

  componentDidMount() {
    this.checkLoggedIn()
  }

  async fetchUser() {
    if (this.state.isLoggedIn) {
      let restURL = "http://localhost:8081/race/rest/users/current";
      await fetch(restURL, {
        method: "GET",
        credentials: 'include',
        headers: {
          mode: 'no-cors',
          'Content-Type': 'application/json',
        }
      })
        .then(response => {
          return response.json()
        })
        .then(data => {
          this.setState({ profileData: data });
        })
    }
  }

  handleFile = (e) => {
    const content = e.target.result;
    console.log('file content',  content)
    // You can set content in state and show it in render.
  }
  
  handleChangeFile = (file) => {
    let fileData = new FileReader();
    fileData.onloadend = handleFile;
    fileData.readAsText(file);
  }

  render() {
    if (this.state.isLoggedIn) {
      console.log(this.state.profileData)
      if (this.state.profileData.role === "ADMIN") {
        return (
          <div className="Home">
            <CreateRace />
            <AddCircuit />
          </div>
        );
      } else if (this.state.profileData.role === "ORGANIZOR") {
        return (
          <div className="Home">
            <CreateRace />
          </div>
        );
      }
    } else {
      return (
        <div><h2>Welcome to the app</h2>
        <input type="file" accept=".txt" onChange={e => 
            handleChangeFile(e.target.files[0])} /> 
        </div>
      
      );
    }
  }

}

export default Home;
