import CreateRace from '../components/CreateRace';
import AddCircuit from "../components/AddCircuit";
import { Component } from "react";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      profileData: [],
    }
    this.renderHome = this.renderHome.bind(this);
  }
  async checkLoggedIn() {
    let restURL = "http://localhost:8081/rest/users/isLoggedIn";
    await fetch(restURL, {
      method: "GET",
      credentials: 'include',
      headers: {
        mode: 'no-cors',
        'Content-Type': 'application/json',
      }
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        this.setState({ isLoggedIn: data });
      })
    this.fetchUser()
  }

  componentDidMount() {
    this.checkLoggedIn()
  }

  async fetchUser() {
    if (this.state.isLoggedIn) {
      let restURL = "/rest/users/current";
      await fetch(restURL, {
        method: "GET",
        credentials: 'include',
        headers: {
          mode: 'no-cors',
          'Content-Type': 'application/json',
        }
      })
        .then(response => {
          return response.json()
        })
        .then(data => {
          this.setState({ profileData: data });
        })
    }
  }
  
  renderHome(){
      if (this.state.profileData.role === "ADMIN") {
        return (
          <div className="Home">
            <CreateRace />
            <AddCircuit />
          </div>
        );
      } else if (this.state.profileData.role === "ORGANIZER") {
        return (
          <div className="Home">
            <CreateRace />
          </div>
        );
      }
     else {
      return (
        <div>
          <h2>Welcome to the app</h2>
          
        </div>
      );
    }
  }
  
  render() {
    return(
      <this.renderHome/>
    )
  }

}

export default Home;
