import './CreateRace.css';
import React, { Component } from "react";

class CreateRace extends Component {


    constructor(props) {
        super(props);
        this.state = {
            name: '',
            circuitId: -1,
            date: "",
            eorDate: "",
            classes: [],
            maxDrivers: 0,
            fetchedCircuits: [],
            circuitOptions: []
        }
        this.createRace = this.createRace.bind(this);
    }

    async fetchCircuits() {
        const restURL = "http://localhost:8081/race/rest/circuits/";
        await fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "GET",
                'Content-Type': 'application/json'
            }
        }).then(response => {
            return response.json()
        }).then(data => {
            this.setState({ fetchedCircuits: data });
        })
        this.state.fetchedCircuits.forEach(circuit => {
            console.log(circuit.name);
            this.setState(prevState => 
                ({ circuitOptions: [prevState.circuitOptions, <option value={circuit.id}>{circuit.name}</ option>] }
            ));
        })
    }
    createRace() {
        const restURL = "http://localhost:8081/race/rest/races/";
        fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "POST",
            },
            body: {

            }
        })
    }
    handleChangeName(value) {
        this.setState({ name: value });
    }
    handleChangeCircuit(value) {
        this.setState({ circuitId: value });
    }
    handleChangeDate(value) {
        this.setState({ date: value });
    }
    handleChangeEorDate(value) {
        this.setState({ eorDate: value });
    }
    componentDidMount() {
        this.fetchCircuits();
    }

    render() {
        return (
            <div className="CreateRace">
                <h2>Create a new race</h2>
                <div className="name">
                    <p>Name : </p>
                    <input type="text" value={this.state.name}
                        className="name-input"
                        onChange={event => this.handleChangeName(event.target.value)} />
                </div>
                <div className="circuit">
                    <p>Circuit : </p>
                    <select name="circuit-select" className="circuit-select"
                        onChange={event => this.handleChangeCircuit(event.target.value)}>
                        {this.state.circuitOptions}
                    </select>
                </div>
                <div className="date">
                    <p>Date : </p>
                    <input type="date" value={this.state.date}
                        className="date-input"
                        onChange={event => this.handleChangeDate(event.target.value)} />
                </div>
                <div className="eorDate">
                    <p>End of registrations : </p>
                    <input type="date" value={this.state.eorDate}
                        className="eorDate-input"
                        onChange={event => this.handleChangeEorDate(event.target.value)} />
                </div>
                <div className="maxDrivers">
                    <p>End of registrations : </p>
                    <input type="number" value={this.state.date}
                        className="eorDate-input"
                        onChange={event => this.handleChangeEorDate(event.target.value)} />
                </div>
                <button className="create-button" onClick={this.createRace}>Create race</button>
            </div>
        )
    }
}

export default CreateRace;
