import './Login.css';
import React, { Component } from "react";

class Login extends Component {


    constructor(props) {
        super(props);
        this.state = {
            username : "",
            password : ""
        }
        this.handleChangePass = this.handleChangePass.bind(this);
        this.handleChangeUsername = this.handleChangeUsername.bind(this);
    }

    componentDidMount() {
        //this.fetchData()
        //console.log(this.state.profileData)
    }
    
    sendLogin(){
        const restURL = "http://localhost:8081/race/rest/login/"+this.state.username+"/";
        fetch(restURL, {
            headers: {
                mode: 'no-cors',
                method: "POST",
                'Content-Type': 'application/json',
            }
        })
    }

    handleChangePass(e){
        this.setState({password : e.target.value});
    }
    handleChangeUsername(e){
        this.setState({username : e.target.value});
    }
    render() {
        return (
            <div className="Login">
                <h2>Login</h2>
                <div className="username">
                    <p>Username : </p>
                    <input type="text" className="username-input" onChange={this.handleChangeUsername}/>
                </div>
                <div className="password">
                    <p>Password : </p>
                    <input type="password" className="password-input" onChange={this.handleChangePass}/>
                </div>
                
                <button className="login-button" onClick={this.sendLogin}>Login</button>
            </div>
        )
    }
}

export default Login;
