import './MyProfile.css';
import React, { Component } from "react";

class MyProfile extends Component {


    async fetchData(){
        const restURL = "http://localhost:8081/race/rest/users/current";
        await fetch(restURL, {
            method: "GET",
            headers: {
                mode: 'no-cors',
                'Content-Type': 'application/json',
            }
        })
            .then(response => {
                return response.json()
            })
            .then(data => {
                this.setState({profileData : data});
            })
    }

    constructor(props) {
        super(props);
        this.state = {
            profileData : []
        }
    }

    componentDidMount() {
        //console.log(this.state.profileData)
    }
    render() {
        return (
            <div className="MyProfile">
                <h2>My Profile</h2>
                <div className="firstName">
                    <p>First name : </p>
                    <input type="text" value={this.state.profileData.firstName} className="firstName-input" />
                </div>
                <div className="lastName">
                    <p>Last name : </p>
                    <input type="text" value={this.state.profileData.lastName} className="lastName-input" />
                </div>
                <div className="email">
                    <p>Email : </p>
                    <input type="text" className="email-input" />
                </div>
                <div className="username">
                    <p>Username : </p>
                    <input type="text" value={this.state.profileData.username} className="username-input" />
                </div>
                <div className="age">
                    <p>Age : </p>
                    <input type="number" value={this.state.profileData.age} className="age-input" />
                </div>
                <button className="edit" onClick={this.fetchData()}>Edit</button>
            </div>
        )
    }
}

export default MyProfile;
